import React, {Component, useState} from "react";
import axios from 'axios';
import PropTypes from 'prop-types';
import { useParams } from 'react-router-dom';
import { withRouter } from "react-router";
import { isAccordionItemSelected } from "react-bootstrap/esm/AccordionContext";


class ViewEHR extends Component{

    constructor(props){
        super(props)
        this.state = {
          record : {},
          pid : '',
          cid : ''
        }
      }




static propTypes = {
    match: PropTypes.object.isRequired,
    location: PropTypes.object.isRequired,
    history: PropTypes.object.isRequired
};


getCookie(cName) {
    const name = cName + "=";
    const cDecoded = decodeURIComponent(document.cookie); //to be careful
    const cArr = cDecoded .split('; ');
    let res;
    cArr.forEach(val => {
        if (val.indexOf(name) === 0) res = val.substring(name.length);
    })
    return res;
  }

 componentDidMount(){
    console.log("Calling")

        const token = this.getCookie('doctor_cookie');
        const headers = { 
            "Content-Type": "application/json" ,
            "Access-Control-Allow-Origin": "*",
            "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
        };
        axios.get('http://localhost:8081/get-ehr/'+this.props.match.params.patientId+'/'+this.props.match.params.consentId,  
        { 
            headers: {
            'Authorization': `Bearer ${token}` 
          }  
        })
        // .then(response => 
        //   {
        //     console.log(response);
        //     this.setState({pid:this.props.match.params.patientId,cid:this.props.match.params.consentId})
        //     this.setState({records: response.data});
        //   }
        // )
        .then(response => {
            this.setState({
            record: response.data    
            })
            console.log(this.state.record.episodesDTOList);
        });





} 

    render(){
        const {match,location,history} = this.props;

        //var { record } = this.state;

        console.log("Params" + this.props.match.params.patientId);
        //console.log(record)
        return <div>
            {/* {record} */}
            {/* {this.state.pid}
            {this.state.cid} */}

            {/* {this.state.records.ehr_id} */}

            <ul>
                <li>
                    Ehr_id: {this.state.record.ehr_id}
                    Episodes:
                    {
                        this.state.record.episodesDTOList.map(episode => (
                            <li>
                                Episode Name: {episode.episodeName}
                                Encounters:{
                                    episode.encounters.map(encounter => (
                                        <li>
                                            Encounter Id: {encounter.encounterId}
                                            Doctor Name: {encounter.doctorName}
                                            OP Records:{
                                                encounter.op_records.map(op_record => (
                                                    <li>
                                                        Diagnosis: {op_record.diagnosis}
                                                        Time: {op_record.timestamp}
                                                        Details: {op_record.recordDetails}
                                                    </li>
                                                ))
                                            }
                                        </li>
                                    ))
                                }
                            </li>
                        ))
                    }
                </li>
            </ul>

        </div>
    }
}
const CreateConsentWithRouter = withRouter(ViewEHR);
export default CreateConsentWithRouter;
